# Generate all months from 2001-01 to 2025-09
import csv
from datetime import datetime, timedelta
def generate_dates():
    dates = []
    year, month = 2001, 1
    while year < 2025 or (year == 2025 and month <= 9):
        dates.append(f"{year:04d}-{month:02d}-01")
        month += 1
        if month > 12:
            month = 1
            year += 1
    return dates

dates = generate_dates()
print(f"\n Generated {len(dates)} months from 2001-01-01 to 2025-09-01\n")

